package basicsOfJava;

public class Demojava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Welcome to Selenium Automation Testing");  // strings should be in double quotation
		System.out.println(1);
		System.out.println(2.3);   // decimal or float
		System.out.println('c');    // character should be in single quotation
		System.out.println(10+4);
		System.out.println(10-4);
		System.out.println(10*4);
		System.out.println(10/2);
		

	}

}
